class Interval{constructor(a,b,c=!0){let t=this;t.e=0;t.f=a;t.p=!1;t.d=b;t.s=!1;t.start=()=>{if(t.s)return;t.s=!0;t.e=Date.now()+t.d;t.p=!1;t.update();};t.update=()=>{if(!t.e)t.e=Date.now()+t.d;let q=Date.now()-t.e;if(!t.paused)t.f();t.e+=t.d;setTimeout(()=>t.update(),Math.max(0,t.d-q));};t.pause=()=>t.p=!0;t.resume=()=>t.p=!1;c?t.start():t.p=!0;}}